include('shared.lua')function ENT:Draw()	-- Pre draw on the client.	local arsenal,func = self:Fo_ArsenalFunction( "PreDraw" )	if ( arsenal and func ) then func( arsenal, self:Fo_GetOwner(), self ) end	arsenal = nil	func = nil		-- Draw the model.	self:DrawModel()		-- Post arsenal draw.	local arsenal,func = self:Fo_ArsenalFunction( "PostDraw" )	if ( arsenal and func ) then func( arsenal, self:Fo_GetOwner(), self ) end	end