include('shared.lua')if ( fo.EnableDynamicLights ) then function ENT:Think()	self.BaseClass.Think( self )		-- Check if the light exists or not.	if ( not IsValid( self.dlight ) ) then self.dlight = DynamicLight( self:EntIndex() ) end		-- Draw the light.	local dlight = self.dlight	dlight.Pos = self:GetPos() + Vector( 0, 0, 10 )	dlight.r = 255	dlight.g = 150	dlight.b = 100	dlight.Brightness = 1	dlight.MinLight = 0.1	dlight.Size = 128	dlight.Decay = 0	dlight.DieTime = CurTime() + 2	dlight.Style = 1end end