include('shared.lua')if ( fo.EnableDynamicLights ) then function ENT:Think()		self.BaseClass.Think( self )		-- Check if the light exists or not.	if ( not IsValid( self.dlight ) ) then self.dlight = DynamicLight( self:EntIndex() ) end		-- Draw the light.	local dlight = self.dlight	dlight.Pos = self:GetPos() + self:GetAngles():Forward() * 10	dlight.r = 255	dlight.g = 255	dlight.b = 255	dlight.Brightness = 1	dlight.MinLight = 0.5	dlight.Size = 64	dlight.Dir = self:GetAngles():Forward() * 10	dlight.InnerAngle = 45	dlight.OuterAngle = 50	dlight.Decay = 0	dlight.DieTime = CurTime() + 2	dlight.Style = 1end end